          customFields?: string;
          /** Email address that will receive the confirmation and the newsletter. */
          email: string;
          /** First name */
          firstName?: string;
          /** Identifier of the language. */
          languageId?: unknown;
          /** Last name */
          lastName?: string;
          /** Defines what should be done. */
          option: unknown;
          /** Identifier of the salutation. */
          salutationId?: unknown;
